scripts.script:
                            
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def filesJson = [:]
def jsonSlurper = new JsonSlurper()
new File(project.build.directory,"report").eachFileRecurse() {
  if(it.name.endsWith('.json')) {
      def json = jsonSlurper.parseText(it.text)
      def key = "${json.id[0]}"
      def value = json[0]
      if(filesJson.containsKey(key)){
         value = filesJson.get(key)
         value.elements.addAll(json[0].elements)
        }
      filesJson.put(key, value)
    }
  }

def consolidatedJsonReports = new File(new File (project.build.directory,"report"),"consolidated_json")

if (consolidatedJsonReports.exists()) {
    consolidatedJsonReports.deleteDir()
}
consolidatedJsonReports.mkdirs()

final FileTreeBuilder treeBuilder = new FileTreeBuilder(consolidatedJsonReports)

filesJson.each { k,v ->
 treeBuilder.file("${k}.json") {
    def json = JsonOutput.toJson([v])
    write JsonOutput.prettyPrint(json)
  }
}
